import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";
import { useSelector } from "react-redux";
import Carousel from "react-grid-carousel";
import { red } from "@mui/material/colors";

const CategoryBox = styled.div`
  margin: 10px;
  border: 3px solid #0003;
  padding: 20px;
  height: 250px;
  background: black;
`;

const CarouselContainer = styled.div`
  margin: 40px 0;
`;

const Title = styled.h2`
  font-family: "Bungee", cursive;
  font-family: "Roboto", sans-serif;
  color: white;
`;

const Author = styled.h5`
  font-family: "Bungee", cursive;
  font-family: "Roboto", sans-serif;
  color: white;
`;

const Paragraph = styled.p`
  font-family: "Bungee", cursive;
  font-family: "Poiret One", cursive;
  font-family: "Roboto", sans-serif;
  color: white;
`;

const Section = styled.h3``;

export default function HomeCategories() {
  const [homeData, setHomeData] = useState([]);
  const loading = useSelector((state) => state.loading);

  useEffect(() => {
    fetchHomeList();
    // eslint-disable-next-line
  }, []);

  const fetchHomeList = async () => {
    const response = await axios
      .get(
        "https://api.nytimes.com/svc/topstories/v2/home.json?api-key=2pAbKOEz8ASuAknW3ktpDO4BUGph89k2"
      )
      .catch((err) => {
        console.log(err);
      });

    setHomeData(response.data.results);
  };

  const MyDot = ({ isActive }) => (
    <span
      style={{
        height: isActive ? "8px" : "5px",
        width: isActive ? "8px" : "5px",
        background: "#6e44ff",
        borderRadius: "50%",
      }}
    ></span>
  );

  function varChange(data) {
    let newData = homeData
      .filter((about) => about.section === data)
      .map((topStories) => {
        const { title, abstract, byline, url } = topStories;
        return (
          <Carousel.Item>
            <CategoryBox>
              <Title>{title}</Title>
              <Author>{byline}</Author>
              <Paragraph>{abstract}</Paragraph>
              <a href={url} target="_blank" rel="noopener noreferrer">
                LINK
              </a>
            </CategoryBox>
          </Carousel.Item>
        );
      });
    return newData;
  }

  return (
    <>
      {!loading ? (
        <div style={{ width: "90%", margin: "0 auto" }}>
          <CarouselContainer>
            <div
              style={{
                boxShadow: " 1px 3px 20px 3px black",
              }}
            >
              <div
                style={{
                  display: "flex",
                  justifyContent: "center",
                }}
              >
                <Title>
                  Top Stories US <i class="fas fa-newspaper"></i>
                </Title>
              </div>

              <Carousel cols={2} rows={1} gap={10} loop>
                {varChange("us")}
              </Carousel>
            </div>
          </CarouselContainer>
          <CarouselContainer>
            <div
              style={{
                boxShadow: " 1px 3px 20px 3px black",
              }}
            >
              <div
                style={{
                  display: "flex",
                  justifyContent: "center",
                }}
              >
