import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";
import { useSelector } from "react-redux";
import Carousel from "react-grid-carousel";

const CategoryBox = styled.div`
  margin: 10px;
  border: 1px solid #0003;
  padding: 20px;
`;

const CarouselContainer = styled.div`
  margin: 50px 0;
`;

const Title = styled.h2`
  font-family: "Bungee", cursive;
  font-family: "Roboto", sans-serif;
`;

const Author = styled.h5`
  font-family: "Bungee", cursive;
  font-family: "Roboto", sans-serif;
`;

const Paragraph = styled.p`
  font-family: "Bungee", cursive;
  font-family: "Poiret One", cursive;
  font-family: "Roboto", sans-serif;
`;

const Section = styled.h3``;

export default function HomeCategories() {
  const [homeData, setHomeData] = useState([]);
  const loading = useSelector((state) => state.loading);

  useEffect(() => {
    fetchHomeList();
    // eslint-disable-next-line
  }, []);

  const fetchHomeList = async () => {
    const response = await axios
      .get(
        "https://api.nytimes.com/svc/topstories/v2/home.json?api-key=2pAbKOEz8ASuAknW3ktpDO4BUGph89k2"
      )
      .catch((err) => {
        console.log(err);
      });

    setHomeData(response.data.results);
  };

  const MyDot = ({ isActive }) => (
    <span
      style={{
        height: isActive ? "8px" : "5px",
        width: isActive ? "8px" : "5px",
        background: "#6e44ff",
        borderRadius: "50%",
      }}
    ></span>
  );

  function varChange(data) {
    let newData = homeData
      .filter((about) => about.section === data)
      .map((topStories) => {
        const { title, abstract, byline, url } = topStories;
        return (
          <Carousel.Item>
            <CategoryBox>
              <Title>{title}</Title>
              <Author>{byline}</Author>
              <Paragraph>{abstract}</Paragraph>
              <a href={url} target="_blank" rel="noopener noreferrer">
                LINK
              </a>
            </CategoryBox>
          </Carousel.Item>
        );
      });
    return newData;
  }

  return (
    <>
      {!loading ? (
        <div style={{ width: "90%", margin: "0 auto" }}>
          <CarouselContainer>
            <div
              style={{
                boxShadow: " 1px 3px 20px 3px black",
              }}
            >
              <div style={{ margin: "10px 20px" }}>
                <Title>
                  Top Stories US <i class="fas fa-newspaper"></i>
                </Title>
              </div>

              <Carousel cols={2} rows={1} gap={10} loop>
                {varChange("us")}
              </Carousel>
            </div>
          </CarouselContainer>
          <CarouselContainer>
            <div
              style={{
                boxShadow: "3px 3px 3px  black",
              }}
            >
              <h2>Technology</h2>
              <Carousel cols={2} rows={1} gap={20} loop showDots dot={MyDot}>
                {varChange("technology")}
              </Carousel>
            </div>
          </CarouselContainer>
          <CarouselContainer>
            <div
              style={{
                boxShadow: "3px 3px 3px  black",
              }}
            >
              <h2>
                World News <i class="fas fa-globe"></i>{" "}
                <i class="fas fa-newspaper"></i>{" "}
              </h2>
              <Carousel cols={2} rows={1} gap={20} loop showDots dot={MyDot}>
                {varChange("world")}
              </Carousel>
            </div>
          </CarouselContainer>
          <CarouselContainer>
            <div
              style={{
                boxShadow: "3px 3px 3px  black",
              }}
            >
              <h2>
                Travel Guide <i class="fas fa-plane"></i>
              </h2>

              <Carousel cols={2} rows={1} gap={20} loop showDots dot={MyDot}>
                {varChange("travel")}
              </Carousel>
            </div>
          </CarouselContainer>
          <CarouselContainer>
            <div
              style={{
                boxShadow: "3px 3px 3px  black",
              }}
            >
              <h2>
                Science <i class="fas fa-atom"></i>
              </h2>
              <Carousel cols={2} rows={1} gap={20} loop showDots dot={MyDot}>
                {varChange("science")}
              </Carousel>
            </div>
          </CarouselContainer>
          <CarouselContainer>
            <div
              style={{
                boxShadow: "3px 3px 3px  black",
              }}
            >
              <h2>
                Arts <i class="fas fa-palette"></i>
              </h2>
              <Carousel cols={2} rows={1} gap={20} loop showDots dot={MyDot}>
                {varChange("arts")}
              </Carousel>
            </div>
          </CarouselContainer>
          <CarouselContainer>
            <div
              style={{
                boxShadow: "3px 3px 3px  black",
              }}
            >
              <h2>Opinion Section</h2>
              <Carousel cols={2} rows={1} gap={20} loop showDots dot={MyDot}>
                {varChange("opinion")}
              </Carousel>
            </div>
          </CarouselContainer>
          <CarouselContainer>
            <div
              style={{
                boxShadow: "3px 3px 3px  black",
              }}
            >
              <h2>Magazine</h2>
              <Carousel cols={2} rows={1} gap={20} loop showDots dot={MyDot}>
                {varChange("magazine")}
              </Carousel>
            </div>
          </CarouselContainer>
          <CarouselContainer>
            <div
              style={{
                boxShadow: "3px 3px 3px  black",
              }}
            >
              <h2>
                Business <i class="fas fa-business-time"></i>
              </h2>
              <Carousel cols={2} rows={1} gap={20} loop showDots dot={MyDot}>
                {varChange("business")}
              </Carousel>
            </div>
          </CarouselContainer>
          <CarouselContainer>
            <div
              style={{
                boxShadow: "3px 3px 3px  black",
              }}
            >
              <h2>
                Wellness <i class="fas fa-spa"></i>
              </h2>
              <Carousel cols={2} rows={1} gap={20} loop showDots dot={MyDot}>
                {varChange("well")}
              </Carousel>
            </div>
          </CarouselContainer>
          <CarouselContainer>
            <div
              style={{
                boxShadow: "3px 3px 3px  black",
              }}
            >
              <h2>
                Sports <i class="fas fa-football-ball"></i>
              </h2>
              <Carousel cols={2} rows={1} gap={20} loop showDots dot={MyDot}>
                {varChange("sports")}
              </Carousel>
            </div>
          </CarouselContainer>
        </div>
      ) : (
        <div className="loader-div">
          <div className="loader"></div>
        </div>
      )}
    </>
  );
}
