import styled from "styled-components";
import React, { useEffect, useState } from "react";

import { useSelector } from "react-redux";

import axios from "axios";

const CardContainerHeader = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 20px 20px 0 50px;
  align-items: center;
`;

const SearchInput = styled.input`
  width: 200px;
  height: 30px;
  border-radius: 3px;
  border: 1px solid #0006;
  background: #fff;
  padding: 4px 4px 4px 10px;
  font: 300 1rem barlow;
  box-shadow: 3px 3px 5px #0003;
  outline-color: #28282899;
`;

const CategoryContainer = styled.div``;

const Title = styled.h1`
  font: 400 1.3rem barlow;
  margin: 8px;
`;

const Date = styled.h5`
  margin: 8px;
  font-size: 1rem;
`;

const Summary = styled.h5`
  margin: 8px;
  font-size: 1rem;
`;

const MovieLink = styled.a`
  margin: 8px;
  font-size: 1rem;
  text-decoration: none;
  color: black;
`;

const Rating = styled.h4`
  font-size: 1rem;
  color: red;
`;

const Image = styled.img`
  height: 180px;
  width: 200px;
  border-radius: 40px;
`;

const RatingTitle = styled.h4``;
export default function Categories() {
  const [movies, setMovies] = useState([]);
  const loading = useSelector((state) => state.loading);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetchMovieCategories();
    // eslint-disable-next-line
  }, []);

  const fetchMovieCategories = async () => {
    const response = await axios
      .get(
        `https://api.nytimes.com/svc/movies/v2/reviews/all.json?api-key=B96BsMyHZskb1xX0KJMMsfVweArZ2Q8f`
      )
      .catch((err) => {
        console.log(err);
      });
    setMovies(response.data.results);
  };

  const highlightTitle = (e) => {
    let textToSearch = e.target.value;
    textToSearch = textToSearch.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");
    let pattern = new RegExp(`${textToSearch}`, "gi");

    for (let i = 0; i < movies.length; i++) {
      let title = document.getElementById(movies[i].display_title);
      if (title !== null) {
        title.innerHTML = title.textContent.replace(
          pattern,
          (match) => `<mark>${match}</mark>`
        );
      }
    }
  };

  return (
    <div>
      <CategoryContainer id="movies-category-container">
        {!loading ? (
          <>
            <CardContainerHeader>
              <SearchInput
                type="text"
                placeholder="Search movie titles..."
                onChange={async (e) => {
                  await setSearchTerm(e.target.value);
                  highlightTitle(e);
                }}
              />
            </CardContainerHeader>
            <div
              id="content-box"
              style={{
                display: "flex",
                flexWrap: "wrap",
                gap: "15px",
                margin: "20px 0",
              }}
            >
              {movies
                .sort((a, b) =>
                  a.display_name > b.display_name
                    ? 1
                    : b.display_name > a.display_name
                    ? -1
                    : 0
                )
                .filter((category) => {
                  if (searchTerm === "") {
                    return category;
                  } else if (
                    category.display_title
                      .toLowerCase()
                      .includes(searchTerm.toLowerCase())
                  ) {
                    return category;
                  }
                  return null;
                })
                .map((category, key) => {
                  const {
                    display_title,
                    publication_date,
                    summary_short,
                    link,
                  } = category;

                  let rating = category.mpaa_rating.replace("-", "");
                  return (
                    <div
                      key={key}
                      style={{
                        display: "flex",
                        flex: "1",
                        flexBasis: "30%",
                        justifyContent: "center",
                      }}
                    >
                      <div
                        style={{
                          display: "flex",
                          flexDirection: "column",
                          background: "#0003",
                          height: "450px",
                          border: "2px solid black",
                          width: "80%",
                        }}
                      >
                        <div
                          style={{
                            display: "flex",
                            flexDirection: "column",
                            marginLeft: "20px",
                            overflow: "scroll",
                          }}
                        >
                          <MovieLink target="_blank " href={link.url}>
                            <Title id={display_title}>{display_title}</Title>
                          </MovieLink>
                          <Image src="movie.jpeg"></Image>
                          <Rating className={rating}>{rating}</Rating>
                          <Date>Date - {publication_date}</Date>
                          <Summary>Description - {summary_short}</Summary>
                        </div>
                      </div>
                    </div>
                  );
                })}
            </div>
          </>
        ) : (
          <div className="loader-div">
            <div className="loader"></div>
          </div>
        )}
      </CategoryContainer>
    </div>
  );
}
