import React from "react";
import { useParams } from "react-router";
import { setCategories, setLoading } from "./redux/actions/actions";
import { useDispatch } from "react-redux";

export default function IdPage() {
  const dispatch = useDispatch();

  useEffect(() => {
    fetchBookCategories();
    // eslint-disable-next-line
  }, []);

  const fetchBookCategories = async () => {
    let params = useParams();
    const response = await axios
      .get(
        "https://api.nytimes.com/svc/books/v3/lists.json?list=${params}api-key=FGuAAGxWuiRNhKjRZsrPKUUiSbtOJUG1"
      )
      .catch((err) => {
        console.log(err);
      });
    dispatch(setCategories(response.data.results.lists));
    dispatch(setLoading(false));
  };

  return (
    <div>
      <h1>This is the show page id: </h1>
    </div>
  );
}
